import "core-js/modules/es.array.push.js";
import "core-js/modules/web.url-search-params.size.js";
export default {
  name: "Index",
  data() {
    return {
      activeIndex: "1",
      carouselGoods: [],
      activeName: "all",
      goods: [],
      labels: ["全部", "类别一", "类别二", "类别三"],
      names: ["all", "group-one", "group-two", "group-three"],
      currentPage: 1,
      pageLimits: [10, 20, 30, 40, 50],
      pageSize: 10,
      itemTotal: 10,
      searchName: "",
      loginFlag: false,
      token: null,
      currGoods: {},
      currUser: null
    };
  },
  methods: {
    toLogin() {
      this.$router.push({
        path: "/Login"
      });
    },
    toRegister() {
      this.$router.push({
        path: "/Register"
      });
    },
    handleClick(tab, event) {
      let clickName = tab.name;
      if (clickName === "all") {
        this.searchName = "";
      } else if (clickName === "group-one") {
        this.searchName = "1";
      } else if (clickName === "group-two") {
        this.searchName = "2";
      } else if (clickName === "group-three") {
        this.searchName = "3";
      }
      this.currentPage = 1;
      this.getGoods(this.currentPage, this.limit);
    },
    getGoods(page, limit) {
      this.axios.get("http://localhost:8123/api/goods/search", {
        params: {
          gName: this.searchName,
          page: page,
          limit: limit
        }
      }, {
        emulateJSON: true
      }).then(resp => {
        if (resp.data.message === "ok") {
          this.itemTotal = resp.data.data.total;
          this.goods = resp.data.data.records;
          this.carouselGoods = this.goods.slice(0, 4);
          // console.log(resp);
          // this.goods.forEach((item) => {
          //   console.log(item.gid);
          // });
        }
      }).catch(error => {
        console.log(error);
      });
    },
    handleSizeChange(limit) {
      this.limit = limit;
      this.currentPage = 1;
      this.getGoods(this.currentPage, this.limit);
    },
    handleCurrentChange(val) {
      this.currentPage = val;
      this.getGoods(this.currentPage, this.limit);
    },
    toCarousel() {},
    onBuy(res) {
      console.log(res);
      if (!this.token) {
        this.$message({
          message: "请先登录",
          type: "error"
        });
      } else {
        this.currGoods = res;
        this.$confirm("确认购买后将生成订单信息", "确认信息", {
          distinguishCancelAndClose: true,
          confirmButtonText: "确认购买",
          cancelButtonText: "取消"
        }).then(() => {
          this.buyGoods();
        }).catch(action => {
          this.$message({
            type: "warning",
            message: "已放弃购买"
          });
        });
      }
    },
    toLoginout() {
      console.log(this.token, "<<<<<token");
      this.axios.post("http://localhost:8123/api/user/loginout", null, {
        params: {
          token: this.token
        }
      }).then(resp => {
        console.log(resp);
        localStorage.removeItem("token");
        this.loginFlag = false;
        this.token = null;
        this.$message({
          message: "退出成功",
          type: "success"
        });
      }).catch(error => {
        console.log(error);
      });
    },
    toHome() {
      this.$router.push({
        path: "/Home"
      });
    },
    buyGoods() {
      this.axios.defaults.headers.post["Content-Type"] = "application/x-www-form-urlencoded";
      const params = new URLSearchParams();
      params.append("token", this.token);
      params.append("dAmount", this.currGoods.gprice);
      params.append("dCount", 1);
      params.append("sId", this.currGoods.uid);
      params.append("gId", this.currGoods.gid);
      params.append("bId", this.currUser.uid);
      this.axios.post("http://localhost:8123/api/order/add", params).then(resp => {
        console.log(resp);
        this.getGoods();
        this.$message({
          type: "success",
          message: "订单生成成功，请前往个人中心完成支付  "
        });
        this.currGoods = {};
      }).catch(error => {
        console.log(error);
      });
      this.currGoods = {};
    }
  },
  created() {
    this.getGoods(1, 10);
    let token = localStorage.getItem("token");
    console.log(token);
    if (token === null) {
      this.loginFlag = false;
      this.token = null;
      this.currUser = null;
    } else {
      this.token = token;
      this.loginFlag = true;
      this.currUser = JSON.parse(localStorage.getItem("currUser"));
    }
  }
};
